import React, { useState, useEffect} from 'react'
import Navbar from '../components/Navbar/Navbar'
import Footer from '../components/Footer/Footer'
import './SignInPagina.scss'
import { Link, useHistory } from 'react-router-dom'
import axios from 'axios'


const LoginPagina= (props) => {

  let history = useHistory();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    if (localStorage.getItem('authToken')) {
      history.push("/userdashboard")
    }
  }, [history]);

  const loginHandler = async (e) => {
    e.preventDefault();

    const config = {
      header: {
        "Content-Type": "application/json",
      },
    };

    try {
      const { data } = await axios.post(
        "/api/auth/login",
        {
          email,
          password,
        },
        config
      );
      console.log(data)

      localStorage.setItem("authToken", data.token);
      localStorage.setItem("email", data.user.email);
      localStorage.setItem("username", data.user.username);

      history.push("/userdashboard");
    } catch (error) {
      console.log(error)
      setError(error.response.data.error);
    }
  };

  return (
    <>
      <Navbar />
      <div className="page-content-wrapper">
        <div className="signup-main-wrapper">
          <form onSubmit={loginHandler}>
            <h3 className="main-heading-signup">Acesa a tu cuenta</h3>
            <h2 className="main-subheading-signup">Y disfruta de los mejores precios envíos</h2>
            {error && <span className="login-screen-title" >{error}</span>}

            <label className="form-label">Email</label>
            <input className="form-input" 
                  required 
                  id="name" 
                  value={email} 
                  onChange={(e) => setEmail(e.target.value)} 
                  type="text" 
                  placeholder="Ingresa tu email"/>

            <label className="form-label">Contraseña</label>
            <input className="form-input" 
                  required 
                  id="password" 
                  value={password} 
                  onChange={(e) => setPassword(e.target.value)} 
                  type="password" 
                  placeholder="Ingresa tu contaseña"/>

            <button type="submit" className="btn-red btn-form">Enviar</button>
          <div className="wrapper-sign-in-link">
            <p>Ya tienes una cuenta?</p>
            <Link to="/signin">¡Da click aqui!</Link>
          </div>
          </form>

        </div>
      </div>
    <Footer/>

    </>
  )
}

export default LoginPagina;
