import React, {useState} from "react";
import { Bar } from "react-chartjs-2";
import { CategoryScale } from 'chart.js';
import Chart from 'chart.js/auto';
import styled from "styled-components";

const GuidesChart = (
  {
    dashboardData
  }
) => {

  const yearsShipments = Object.keys(dashboardData.shipments)
  const yearsRecargas = Object.keys(dashboardData.refils)

  const [data, setData] = useState(["0","0","0","300","0","0","0", "0","0","0","0","0"]);
  const [yearsToSelect, setYearsToSelect] = useState(yearsShipments);


  Chart.register(CategoryScale);
  const barChartData = {
    labels: ["Ene", "Feb", "Mar", "Mar","Abr","May","Jun","Jul",
    "Ago", "Sep", "Oct", "Nov", "Dic"
  ],
    datasets: [
      {
        data: data,
        borderColor: "#3333ff",
        backgroundColor: "pink",
        fill: true
      },
    ]
  };

  const barChart = (
    <Bar
      type="bar"
      width={70}
      height={20}
      options={{
        title: {
          display: false,
          text: "COVID-19 Cases of Last 3 Months",
          fontSize: 15
        },
        plugins: {
          legend: {
            display: false
          }
        }

      }}
      data={barChartData}
    />
  );

  function updateChart(e) {
    console.log(e.target.value)
  }


  return (
  <ChartWrapper>
    <SelectWrapper>
      <Select onSelect={(e) => updateChart(e)}>
          { yearsToSelect.map((year) => {
            return <option >{year}</option>
          })}
      </Select>
      <Select>
                    <option onSelect={(e) => updateChart(e)}>
                      Envios
                    </option>
                    <option onSelect={(e) => updateChart(e)}>
                      Recargas
                    </option>
      </Select>
    </SelectWrapper>
    {barChart}
  </ChartWrapper>
  );
};

const Select = styled.select`
  font-family: 'Montserrat', sans-serif;
  font-weight: 500;
  align-self: flex-end;
  margin-right: 30px
`

const SelectWrapper = styled.div`
  display: flex;
  flex-direction: row;
  width: 100%;
  align-items: flex-end;
  justify-content: flex-end;
`

const ChartWrapper = styled.div`
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 50px;
`


export default GuidesChart;
