import React,  { useState, useEffect} from 'react'
import { useParams } from 'react-router'
import Navbar from '../components/Navbar/Navbar'
import './RastreoPagina.scss';
import styled from 'styled-components'
import IconoRastreo from '../assets/Inicio/Icono_rastreo-43.svg'

const RasteroPagina = () => {

  const [loading, setLoading] = useState(true)
  const [trackedGuide, setTrackedGuide] = useState("")
  const [guideInformation, setGuideInformation] = useState()
  const [shipmentHistory, setShipmentHistory] = useState()

  const params = useParams()
  const guideNumber = params.guide
  console.log('getting guide',guideNumber)

  const getGuideInfo = async () => {
    console.log('Getting Guide');
    const body = {
      "clientDetail": {
        "accountName": "rastreo@quiken.mx",
        "apiKey": "QNy1tpJFfmYIOlqF1oiwBy7iE46LXuwb"
      },
      "trackingNumbers": [
        guideNumber
      ]
    }

    const url = 'http://api.quiken.mx/track'
    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify(body)
    })
    const data = await response.json()
    setGuideInformation(data)
    setShipmentHistory(data.data.shipments[0].shipmentHistory)
    setLoading(false)
    console.log(data)
  }


  useEffect(() => {
    console.log(guideNumber)
    getGuideInfo()  
  }, [])


  return (
    <>
      <Navbar/>
      <div className="request-body-wrapper">

        {loading ? 

        <div>...Loading</div> :
        <div>
          <div>
            <h1 className="request-header">Rastreo y Seguimiento</h1>
            <div>
              <div className="div-tracking">
                <p className="rastreo-blue-letters">Ingresa tu número de rastreo:</p>

                <div className="rastreo-input-div">
                  <input className="tracking-number-input" placeholder="Código de rastreo"></input>

                    <PlaceHolderLookUp>
                      <img className="icono-rastreo" src={IconoRastreo}></img>
                    </PlaceHolderLookUp>
                </div>

              </div>
            </div>

            <h1 className="estado-de-pedido">Estado de envio: <span>{guideNumber}</span> </h1>
            <h1 className="request-header">Código de rastreo: {guideNumber}</h1>
          </div>

          <div>
            <h1>Historial de Pedido</h1>

            <table>
              <thead>
                <tr>
                  <th className="table-header-tracking">header1</th>
                  <th className="table-header-tracking">header2</th>
                  <th className="table-header-tracking">header3</th>
                </tr>
              </thead>
              <tbody>
                {shipmentHistory.map((step) => {
                  return (
                  <tr>
                    <td className="table-data-tracking">{step.status}</td>
                    <td>{step.description}</td>
                    <td>{step.date}</td>
                  </tr>)
                })}
              </tbody>
            </table>

          </div>
        </div>

        }

      </div>

    </>  
  )
}

const PlaceHolderLookUp = styled.div`
height: 50px;
width: 50px;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center ;
background-color: red;
background-image: url("./");
border-radius: 5px;
border: solid 1px black;
@media (max-width: 768px) {
    height: 30px;
    width: 50px;
    align-self: center;
    justify-self: center;

  } 
`

export default RasteroPagina;
