import React, {useState, useEffect, useRef} from "react";
import { Bar } from "react-chartjs-2";
import { CategoryScale } from 'chart.js';
import Chart from 'chart.js/auto';
import styled from "styled-components";

const GuidesChart = (
  {
    dashboardData
  }
) => {

  const yearTag = useRef(null);
  const typeTag = useRef(null);

  const yearsShipments = Object.keys(dashboardData.shipments)
  const yearsRecargas = Object.keys(dashboardData.refils)

  const [loading, setLoading] = useState(true);
  const [data, setData] = useState(["0","0","0","300","0","0","0", "0","0","0","0","0"]);
  const [yearsToSelect, setYearsToSelect] = useState(yearsShipments);


  Chart.register(CategoryScale);
  const barChartData = {
    labels: ["Ene", "Feb", "Mar", "Mar","Abr","May","Jun","Jul",
    "Ago", "Sep", "Oct", "Nov", "Dic"
  ],
    datasets: [
      {
        data: data,
        borderColor: "#3333ff",
        backgroundColor: "pink",
        fill: true
      },
    ]
  };

  function updateChart(e, action) {
    const year = yearTag.current.value
    const type = typeTag.current.value
    let newData = Array(12).fill(0)
    console.log(dashboardData)
    if (type == "Envios") {
      dashboardData.shipments[year].forEach((entry) => {
    switch (entry.Mes) {
      case "January":
        newData[0] = entry.total
      case "February":
        console.log("YIPI")
        newData[1] = entry.total
      case "March":
        newData[2] = entry.total
      case "April":
        newData[3] = entry.total
      case "May":
        newData[4] = entry.total
      case "June":
        newData[5] = entry.total
      case "July":
        newData[6] = entry.total
      case "August":
        newData[7] = entry.total
      case "September":
        newData[8] = entry.total
      case "October":
        newData[9] = entry.total
      case "November":
        newData[10] = entry.total
      case "December":
        newData[11] = entry.total 
      }
      })  
    }

    if (type == "Recargas") {
        console.log("Luk")
    }
    console.log(newData)
    setData(newData)
  }

  const barChart = (
    <Bar
      type="bar"
      width={70}
      height={20}
      options={{
        title: {
          display: false,
          text: "COVID-19 Cases of Last 3 Months",
          fontSize: 15
        },
        plugins: {
          legend: {
            display: false
          }
        }

      }}
      data={barChartData}
    />
  );

  useEffect(() => {
    setLoading(false)
    console.log("runnig this")
  }, [loading]);

  console.log(yearsToSelect)

  return (
  <ChartWrapper>
    {
        loading ? <></> :
        <>
        <SelectWrapper>
          <Select ref={yearTag} onChange={(e) => updateChart(e, {type: "year"})}>
              { yearsToSelect.map((year) => {
                return <option >{year}</option>
              })}
          </Select>
          <Select ref={typeTag} onChange={(e) => updateChart(e, { type: "graph"})}>
                        <option>
                          Envios
                        </option>
                        <option>
                          Recargas
                        </option>
          </Select>
        </SelectWrapper>
        {barChart}
        </>
    }

  </ChartWrapper>
  );
};

const Select = styled.select`
  font-family: 'Montserrat', sans-serif;
  font-weight: 500;
  align-self: flex-end;
  margin-right: 30px
`

const SelectWrapper = styled.div`
  display: flex;
  flex-direction: row;
  width: 100%;
  align-items: flex-end;
  justify-content: flex-end;
`

const ChartWrapper = styled.div`
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 50px;
`


export default GuidesChart;
