                </div>
            </div>

            {/* PACKAGE SECTION */}
            <div>
            <h2 className="subheading-form-create-guide">¿Dimensiones del paquete?</h2>
                <div>
                    <div>
                    <input className="input-create-guide-form" value={packageLenght} onChange={(event) => setPackageLenght (event.target.value)} type="text" placeholder="Largo (cm)" />
                    {Object.keys(packageLenghtError).map((key) => {return <div>{packageLenghtError[key]}</div>})}
                </div>

                <div>
                    <input className="input-create-guide-form" value={packageWidth} onChange={(event) => setPackageWidth(event.target.value)} type="email" placeholder="Ancho (cm)" />
                    {Object.keys(packageWidthError).map((key) => {return <div>{packageWidthError[key]}</div>})}
                </div>                

                <div>
                    <input className="input-create-guide-form" value={packageHeight} onChange={(event) => setPackageHeight(event.target.value)} type="text" placeholder="Alto (cm)" />
                    {Object.keys(packageHeightError).map((key) => {return <div>{packageHeightError[key]}</div>})}
                </div>

                    <h4>Peso del paquete que envías</h4>

                <div>
                    <input className="input-create-guide-form" value={packageWeight} onChange={(event) => setPackageWeight(event.target.value)} type="text" placeholder="Peso (kg)" />
                    {Object.keys(packageWeightError).map((key) => {return <div>{packageWeightError[key]}</div>})}
                </div>

                    <h4>¿Que envías?</h4>

                <div>
                    <input className="input-create-guide-form" value={packageDescription} onChange={(event) => {
                      setPackageDescription(event.target.value)}} type="text" placeholder="Descripción" />
                      {Object.keys(packageDescriptionError).map((key) => {return <div>{packageDescriptionError[key]}</div>})}
                </div>

                <div>
                    <label>Costo de tu Guia</label>
                    <br></br>
                    <label>{guideCost}.00</label>
                </div>

            </div>
            </div>

            <button onClick={(event) => {handleSubmit(event)}}>Siguiente</button>

        </div>
    )
}

export default CreateGuidePagina;
