import React, { useState} from 'react'
import Navbar from '../components/Navbar/Navbar'
import './SignUpPagina.scss'
import { Link } from 'react-router-dom'
import { set } from 'mongoose'


const SignUpPagina = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isPending, setIsPending] = useState(false);
  const [error, setError] = useState("")

  function registerNewUser (blog) {
    console.log('Creating User');
    fetch('/api/auth/register', {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)  
    }).then((response) => {
      response.json()
      .then((data) => {
        console.log('Data: ', data.token)
        return data.token
      })
    })
  }

  const registerHandler = async (e) => {  
    e.preventDefault();
    const blog = {username, email, password};
    console.log(JSON.stringify(blog), 'sending this data to backend')

    setIsPending(true)

    if (password !== confirmPassword) {
      setPassword("");
      setConfirmPassword("");
      setTimeout(() => {
        setError("")
      }, 5000);
      return setError("Passwords do not match")
    } else {

      try {
        console.log(await registerNewUser(blog))



        localStorage.setItem('authToken', 'tokenbrody')
      } catch(error) {

      }
    }


  }

  return (
    <>
      <Navbar />
      <div className="page-content-wrapper">
        <div className="signup-main-wrapper">
          <form onSubmit={registerHandler}>
            <label className="form-label">Usuario</label>
            <input className="form-input" 
                  required 
                  id="name" 
                  value={username} 
                  onChange={(e) => setUsername(e.target.value)} 
                  type="text" 
                  placeholder="Ingresa tu nombre usuario"/>

            <label className="form-label">Email</label>
            <input className="form-input" 
                  required 
                  id="name" 
                  value={email} 
                  onChange={(e) => setEmail(e.target.value)} 
                  type="text" 
                  placeholder="Ingresa tu email"/>

            <label className="form-label">Contraseña</label>
            <input className="form-input" 
                  required 
                  id="password" 
                  value={password} 
                  onChange={(e) => setPassword(e.target.value)} 
                  type="password" 
                  placeholder="Ingresa tu contaseña"/>

            <label className="form-label">Confirmar Contraseña</label>
            <input className="form-input" 
                  required 
                  id="password" 
                  value={confirmPassword} 
                  onChange={(e) => setConfirmPassword(e.target.value)} 
                  type="password" 
                  placeholder="Confirma tu contraseña"/>

            <button type="submit" onClick={registerHandler} className="btn-red btn-form">Registrarme</button>
          </form>

          <div className="wrapper-sign-in-link">
            <p>Ya tienes una cuenta?</p>
            <Link to="/signin">¡Da click aqui!</Link>
          </div>
        </div>
      </div>

    </>
  )
}

export default SignUpPagina;
