import React, { useState} from 'react'
import Navbar from '../components/Navbar/Navbar'
import './SignUpPagina.scss'
import { Link } from 'react-router-dom'


const SignUpPagina = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isPending, setIsPending] = useState(false);

  const registerHandler = (e) => {  
    e.preventDefault();
    const blog = {username, email, password};
    console.log(JSON.stringify(blog), 'sending this data to backend')

    setIsPending(true)

    fetch('http://localhost:5000/api/auth/register', {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)  
    }).then(() => {
      console.log('new blog added')
      setIsPending(false)
    })
    .then((res) => {
      console.log(res)
    })
  }

  return (
    <>
      <Navbar />
      <div className="page-content-wrapper">
        <div className="signup-main-wrapper">
          <form onSubmit={registerHandler}>
            <label className="form-label">Usuario</label>
            <input className="form-input" 
                  required 
                  id="name" 
                  value={username} 
                  onChange={(e) => setUsername(e.target.value)} 
                  type="text" 
                  placeholder="Ingresa tu nombre usuario"/>

            <label className="form-label">Email</label>
            <input className="form-input" 
                  required 
                  id="name" 
                  value={email} 
                  onChange={(e) => setEmail(e.target.value)} 
                  type="text" 
                  placeholder="Ingresa tu email"/>

            <label className="form-label">Contraseña</label>
            <input className="form-input" 
                  required 
                  id="password" 
                  value={password} 
                  onChange={(e) => setPassword(e.target.value)} 
                  type="password" 
                  placeholder="Ingresa tu contaseña"/>

            <label className="form-label">Confirmar Contraseña</label>
            <input className="form-input" 
                  required 
                  id="password" 
                  value={confirmPassword} 
                  onChange={(e) => setConfirmPassword(e.target.value)} 
                  type="password" 
                  placeholder="Confirma tu contraseña"/>

            <button type="submit" onClick={registerHandler} className="btn-red btn-form">Registrarme</button>
          </form>

          <div className="wrapper-sign-in-link">
            <p>Ya tienes una cuenta?</p>
            <Link to="/signin">¡Da click aqui!</Link>
          </div>
        </div>
      </div>

    </>
  )
}

export default SignUpPagina;
