import React, { useState, useEffect} from 'react'
import Navbar from '../components/Navbar/Navbar'
import './SignInPagina.scss'
import { Link, useHistory } from 'react-router-dom'
import axios from 'axios'

const LoginPagina= (props) => {

  let history = useHistory();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    if (localStorage.getItem('authToken')) {
      history.push("/")
    }
  }, [history]);

  const loginHandler = async (e) => {
    e.preventDefault();

    const config = {
      header: {
        "Content-Type": "application/json",
      },
    };

    if (password !== confirmPassword) {
      setPassword("");
      setConfirmPassword("");
      return setError("Passwords do not match");
    }

    try {
      const { data } = await axios.post(
        "/api/auth/register",
        {
          username,
          email,
          password,
        },
        config
      );

      localStorage.setItem("authToken", data.token);

      history.push("/");
    } catch (error) {
      console.log(error)
      setError(error.response.data.error);
    }
  };

  return (
    <>
      <Navbar />
      <div className="page-content-wrapper">
        <div className="signup-main-wrapper">
          <form onSubmit={registerHandler}>
            <h3>Registrate</h3>
            {error && <span className="register-screen-title" >{error}</span>}
            <label className="form-label">Usuario</label>
            <input className="form-input" 
                  required 
                  id="name" 
                  value={username} 
                  onChange={(e) => setUsername(e.target.value)} 
                  type="text" 
                  placeholder="Ingresa tu nombre usuario"/>

            <label className="form-label">Email</label>
            <input className="form-input" 
                  required 
                  id="name" 
                  value={email} 
                  onChange={(e) => setEmail(e.target.value)} 
                  type="text" 
                  placeholder="Ingresa tu email"/>

            <label className="form-label">Contraseña</label>
            <input className="form-input" 
                  required 
                  id="password" 
                  value={password} 
                  onChange={(e) => setPassword(e.target.value)} 
                  type="password" 
                  placeholder="Ingresa tu contaseña"/>

            <label className="form-label">Confirmar Contraseña</label>
            <input className="form-input" 
                  required 
                  id="password" 
                  value={confirmPassword} 
                  onChange={(e) => setConfirmPassword(e.target.value)} 
                  type="password" 
                  placeholder="Confirma tu contraseña"/>

            <button type="submit" onClick={registerHandler} className="btn-red btn-form">Registrarme</button>
          </form>

          <div className="wrapper-sign-in-link">
            <p>Ya tienes una cuenta?</p>
            <Link to="/signin">¡Da click aqui!</Link>
          </div>
        </div>
      </div>

    </>
  )
}

export default LoginPagina;
