import React, { useState, useEffect } from 'react'
import "./RecargarSaldoPagina.scss"
import Navbar from '../components/Navbar/Navbar'
import Footer from '../components/Footer/Footer'
import CheckoutStripe from '../components/Cart/Checkout'


const RecargarSaldoPagina = () => {

    const items = [
        {id:100, total: 200},
        {id:101, total: 1000},
        {id:102, total: 10000}
    ]

    //Initializing Variables and States
    let cart = [];
    const [paymentMethodSelected, setPaymentMethodSelected] = useState(false)
    const [paymentMethod, setPaymentMethod] = useState("")

    const handleProductClick= (id) => {
        const selectedProduct = items.find(product => product.id === id)
        console.log('Selected Product', selectedProduct)
        cart.push(selectedProduct) 
    }


    const calculateTotal = () => {
        let cartTotal = 0;
        cart.forEach(product => {
            cartTotal += product.total 
        })
        return cartTotal
    }


    useEffect(() => {
        setCartTotal(calculateTotal())
    }, [cart])


    return (
        <div>
            <Navbar/>
            <div className="main-wrapper-recargar-saldo">
                <div className="main-wrapper-comprar-saldo">

                    <div className="wrapper-seleccionador paquetes">
                        <h1 className="saldo-actual-heading">1. Recarga Saldo</h1>

                        <div className="card-saldo-actual">
                            <label className="saldo-actual-subheading">Saldo Actual</label>
                            <h1 className="saldo-actual-big-number">$ 0.00</h1>  
                        </div>

                        <div className="paquetes-de-puntos">
                            <label className="heading-agregar-puntos">Da Click y Agrega Puntos!</label>

                            <div className="card-agrega-credito" 
                            onClick={() => {
                                handleProductClick(100)
                            }}>
                                <label className="agrega-credito-actual-subheading">+ Agregar credito</label>
                                <h1 className="agrega-credito-actual-big-number">$ 200.00</h1>  
                            </div>

                            <div className="card-agrega-credito"
                            onClick={() => {
                                handleProductClick(101)
                            }}
                            >
                                <label className="agrega-credito-actual-subheading">+ Agregar credito</label>
                                <h1 className="agrega-credito-actual-big-number">$ 1000.00</h1>  
                            </div>

                            <div className="card-agrega-credito"
                            onClick={() => {
                                handleProductClick(102)
                            }}
                            >
                                <label className="agrega-credito-actual-subheading">+ Agregar credito</label>
                                <h1 className="agrega-credito-actual-big-number">$ 10000.00</h1>  
                            </div>


                        </div>

                    </div>

                    <div className="wrapper-checkout-puntos">
                    <h1 className="saldo-actual-heading">2. Checkout</h1>

                        <div  className="card-saldo-actual">
                            <label className="saldo-actual-subheading">Saldo por recargar</label>
                            <h1 className="saldo-actual-big-number">$ 0.00</h1>  
                        </div>

                        <div  className="card-saldo-actual">
                            <label className="saldo-actual-subheading">Saldo Nuevo</label>
                            <h1 className="saldo-actual-big-number">$ 0.00</h1>  
                        </div>

                        <h1 className="selecciona-metodo-pago-heading">Selecciona tu metodo de pago</h1>

                        <select className="payment-method-selector-saldo"  onChange={(event) => {
                            setPaymentMethodSelected(!paymentMethodSelected)
                            setPaymentMethod(event.target.value)
                            }}
                            >
                            <option value="">Ninguno</option>
                            <option value="tarjeta">Tarjeta de credito/debito</option>
                        </select>

                    </div>
                </div>

                <div>
                        {
                        paymentMethodSelected ?
                            <div className="checkout-wrapper">
                                <h1 className="metodo-tarjeta-heading">Favor de ingresar la información de su tarjeta.</h1>
                                <h2 className="metodo-tarjeta-subheading">Numero de Tarjeta, Codigo, Fecha de expiración, Codigo Postal</h2>
                                <h2 className="metodo-tarjeta-subheading">Aceptamos todo tipo de tarjeta (Visa, AMEX, MasterCard)</h2>
                                <CheckoutStripe></CheckoutStripe> 
                            </div>
                        :
                        <div></div>
                        }


                </div>


            </div>
            <Footer/>
        </div>
    )
}

export default RecargarSaldoPagina
