    // //GUIDE CONSOLE LOG
    // console.log(
    //     'FullnameSender: ',fullNameSender,<br/>,
    //     'EmailSender: ',emailSender, <br/>, 
    //     'PhoneNumberSender ',phoneNumberSender, <br/>,
