import React,  { useState, useEffect} from 'react'
import { useParams } from 'react-router'
import Navbar from '../components/Navbar/Navbar'
import './RastreoPagina.scss';


const RasteroPagina = () => {

  const [loading, setLoading] = useState(true)
  const [trackedGuide, setTrackedGuide] = useState("")
  const [guideInformation, setGuideInformation] = useState()
  const [shipmentHistory, setShipmentHistory] = useState()

  const params = useParams()
  const guideNumber = params.guide
  console.log('getting guide',guideNumber)

  const getGuideInfo = async () => {
    console.log('Getting Guide');
    const body = {
      "clientDetail": {
        "accountName": "rastreo@quiken.mx",
        "apiKey": "QNy1tpJFfmYIOlqF1oiwBy7iE46LXuwb"
      },
      "trackingNumbers": [
        guideNumber
      ]
    }

    const url = 'http://api.quiken.mx/track'
    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify(body)
    })
    const data = await response.json()
    setGuideInformation(data)
    setShipmentHistory(data.data.shipments[0].shipmentHistory)
    setLoading(false)
    console.log(data)
  }


  useEffect(() => {
    console.log(guideNumber)
    getGuideInfo()  
  }, [])


  return (
    <>
      <Navbar/>
      <div className="request-body-wrapper">

        {loading ? 

        <div>...Loading</div> :
        <div>
          <div>
            <h1 className="request-header">Rastreo de Solicitud</h1>
            <h1 className="request-header"># {guideNumber}</h1>
            <h1 className="request-header">Estatus {shipmentHistory[0].status}</h1>
          </div>

          <div>
            <h1>Historial de Pedido</h1>

            <table>
              <thead>
                <tr>
                  <th>header1</th>
                  <th>header2</th>
                  <th>header3</th>
                </tr>
              </thead>
              <tbody>
                {shipmentHistory.map((step) => {
                  return (
                  <tr>
                    <td>{step.status}</td>
                    <td>{step.statusCode}</td>
                    <td>{step.status}</td>
                  </tr>)
                })}
              </tbody>
            </table>

          </div>
        </div>

        }

      </div>

    </>  
  )
}

export default RasteroPagina;
