                  {/* <option value="Tlaxcala">Tlaxcala</option> */}
                  <option value="VE">Veracruz</option>
                  {/* <option value="Yucatán">Yucatán</option>
                  <option value="Zacatecas">Zacatecas</option> */}
                  </select>
              </LabelInputWrapper>
          </SubsectionWrapper>

          {/* TO PACKAGE INFO */}
