    if (fullNameReceiver.trim().length < 5) {
        fullNameReceiverError.FullNameReceiverShort = "El nombre complete tiene que tener más de 5 caracteres";
        isValid = false;
        console.log("name too short")
      }

    if (fullNameReceiver.trim().match(/[0-9]/)) {
