import { useState, useEffect } from "react";
import { useHistory, Link } from 'react-router-dom'
import axios from "axios";
import "./UserDashboard.scss"
import Navbar from '../components/Navbar/Navbar'
import GuideTable from '../components/UserDashboard/GuideTable'
import Guide from "../../../models/Guide";

const UserDashboard = () => {   

  const [error, setError] = useState("");
  const [privateData, setPrivateData] = useState("");
  const history = useHistory();
  let currentUser = localStorage.getItem('email')
  console.log('currentUser: ', currentUser)

  //Get Fetch Guides Function
  const loadUserGuides = (currentUser) => {
    router.get('/api/user/guides'), (req, res, next) => {
      Guide.find()
      .exec()
      .then(docs => {
        console.log(docs)
      res.status(200).json(docs);
      })
      .catch((err) => {
        console.log(err);
        res.status(500).json({
          error: err
        });
      })
    } 
  };

  //Fetch Private Data
  const fetchPrivateDate = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("authToken")}`,
      },
    };
    try {

      //AQUI VAN LAS RUTAS DE LAS GUIAS
      const { data } = await axios.get("/api/private", config);
      setPrivateData(data.data);



    } catch (error) {
      localStorage.removeItem("authToken");
      localStorage.removeItem("email");
      localStorage.removeItem("username");
      setError("You are not authorized please login");
    }

  };


  useEffect(() => {

    fetchPrivateDate();
  }, []);

  const logoutHandler = () => {
      localStorage.removeItem('authToken');
      localStorage.removeItem('email');
      localStorage.removeItem('username');


      history.push('/signin')
  }

  return error ? (
    <span className="error-message">{error}</span>
  ) : (
    <>  
        <Navbar/>
            <Link to="/createguide">
                <button>Create Guide</button>
            </Link>
            <div>{privateData}</div>
        <GuideTable/>

        <button onClick={logoutHandler}>Logout</button>

    </>
  );
};

export default UserDashboard;
