import React, { useState } from 'react'

const ContactForm = () => {

    let isValid = true;

    //Contact Form States
    const [fullName, setFullName] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [email, setEmail] = useState("");
    const [subject, setSubject] = useState("");
    const [message, setMessage] = useState("")

    //Contact Form Error States
  const [fullNameError, setFullNameError] = useState('');
  const [emailError, setEmailError] = useState('');
  const [phoneNumberError, setPhoneNumberError] = useState('');
  const [subjectError, setSubjectError] = useState('');
  const [messageError, setMessageError] = useState('');

    //Helper Functions for Validation
    function hasLetters (lenght) {
        return /^\d+$/.test(lenght)
      }

    function emailIsValid (email) {
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)
    }

    const contactFormValdiation= () => {
        //Error Form Objects
        const fullNameError = {};
        const phoneNumberError = {};
        const emailError = {};
        const subjectError = {};
        const messageError = {};

        //Name Validation
    if (fullName.trim().length < 5) {
        fullNameError.FullNameSenderShort = "El nombre complete tiene que tener más de 5 caracteres";
        isValid = false;
        console.log("name too short")
      }

      if (fullName.trim().match(/[0-9]/)) {
        fullNameError.FullNameLong = "Favor de ingresar solamente letras";
        isValid = false;
      }
      setFullNameError(fullNameError)

      //Email Validation
      if (emailIsValid(email) == false) {
        emailError.EmailError = "El email no es valido"
        isValid = false;
        console.log("The email is not valid")
      }

      setEmailError(emailError)

      //Phone Validation
      if (hasLetters(phoneNumber) == false) {
        phoneNumberError.PhoneTooShort = "Favor de introducir solo numeros"
        isValid = false;
      }

      if (phoneNumber.trim().length < 10) {
        phoneNumberError.PhoneTooShort = "El télefono tiene que tener minimo 10 digitos"
        console.log("el telefono es muy corto")
        isValid = false;
      }

      setPhoneNumberError(phoneNumberError)

      //Tema Validation
      if (subject.trim().length < 5) {
        subjectError.FieldToShort = "El campo debe tener más de 5 caracteres";
        isValid = false;
      }

      setSubjectError(subjectError)

      if (message.trim().length < 5) {
        messageError.FieldToShort = "El campo debe tener más de 5 caracteres";
        isValid = false;
      }
      setMessageError(messageError)
    }


    const handleContactForm = (e) => {
        e.preventDefault();

        const isValid = contactFormValdiation()

        if (isValid) {
            console.log(
                "fullName: ", fullName, <br/>,
                "PhoneNumber: ", phoneNumber, <br/>,
                "email: ", email, <br/>,
                "subject: ", subject, <br/>,
                "message: ", message, <br/>, 
            )
        }
    }

    return (
     <>
        {/* //Nombre Completo Field */}
        <div>
            <label>Nombre Completo<span> *</span></label>
            <input 
            value={fullName}
            onChange={(event) => setFullName(event.target.value)} 
            type="text"
            placeholder="Juan Gonzalez"
            />
            {Object.keys(fullNameError).map((key) => {
                return <div>{fullNameError[key]}</div>}
            )}
        </div>
        {/* //Telefono Field */}
        <div>
            <label>Teléfono<span> *</span></label>
            <input 
            value={phoneNumber}
            onChange={(event) => setPhoneNumber(event.target.value)} 
            type="text"
            placeholder="81568180"
            />
            {Object.keys(phoneNumberError).map((key) => {
                return <div>{phoneNumberError[key]}</div>}
            )}
        </div>
        {/* //Email Field */}
        <div>
            <label>Correo<span> *</span></label>
            <input 
            value={email}
            onChange={(event) => setEmail(event.target.value)} 
            type="text"
            placeholder="juangonzalez@gmail.com"
            />
            {Object.keys(emailError).map((key) => {
                return <div>{emailError[key]}</div>}
            )}
        </div>
        {/* //Subject Fields */}
        <div>
            <label>Tema<span> *</span></label>
            <input 
            value={subject}
            onChange={(event) => setSubject(event.target.value)} 
            type="text"
            placeholder="información sobre envios locales"
            />
            {Object.keys(subjectError).map((key) => {
                return <div>{subjectError[key]}</div>}
            )}
        </div>
        {/* //Subject Mensaje Field        */}
        <div>
            <label>Mensaje<span> *</span></label>
            <input 
            value={message}
            onChange={(event) => setMessage(event.target.value)} 
            type="text"
            placeholder="Información sobre envios locales"
            />
            {Object.keys(messageError).map((key) => {
                return <div>{messageError[key]}</div>}
            )}
        </div>

        <p>Campos marcados con <span> *</span> son requeridos</p>
        <button className="btn-contact-form" onClick={(event) => handleContactForm(event)} >Enviar</button>
     </>
    )
}

export default ContactForm;
